html:
{% if doc.purchase_order %}
    {% set po = frappe.get_doc("Purchase Order", doc.purchase_order) %}
{% endif %}

{% set address %}{% endset %}
{% if doc.company %}
    {%- set address=frappe.get_doc("Address",{"address_title":doc.company}) -%}
{% endif %}

{% set sa %}{% endset %}
{% if doc.supplier_address %}
    {% set sa=frappe.get_doc("Address",doc.supplier_address) %}
{% endif %}

{% set total_qty=[0] %}
{% set total_amt=[0.00] %}
{% for item1 in doc.items %}
    {% set _ = total_qty.append(total_qty.pop() + item1.qty|int) %}
    {% set _ = total_amt.append(total_amt.pop() + item1.basic_amount|float) %}
{% endfor %}

<!DOCTYPE HTML>
<div class="row">
    <div class="col-xs-9" style="text-align: left;font-size: 10px;">
        For movement of goods, for job-work, as per the provision of Section 143<br>of the Central Goods and Service Tax, Act,2017.
    </div>
    <div class="col-xs-3" style="text-align: right;">
        <b>{{ _("Original for Recipient") }}</b>
    </div>
</div>
<table>
    <tr>
        <td>

                {% if letter_head %}
                    {{ letter_head }}
                {% endif %}

        </td>
    </tr>
</table>
<table>
    <tr>
        <td style="text-align: center; font-size: 12px;">
            <b>DELIVERY CHALLAN FOR JOBWORK</b>
        </td>
    </tr>
</table>
<div style="width:100%;font-size: 15px;">
    <table>
        <tr>
            <td>
                <div class="row">
                    <div class="col-xs-12">
                        <b>{{ _("Challan No.") }}: </b>&nbsp;
