                            {{doc.name}}
                        {% endif %}
                    </div>
                </div>
                <div class="row">
                    <div class="col-xs-12">
                        <b>{{ _("Challan Date") }}: </b>&nbsp;
                        {% if doc.posting_date %}
                            {{doc.get_formatted("posting_date")}}
                        {% endif %}
                    </div>
                </div>
                <div class="row">
                    <div class="col-xs-12">
                        <b>{{ _("PO No.") }}: </b>&nbsp;{% if doc.purchase_order %}{{doc.purchase_order}}{% endif %}
                    </div>
                </div>
                <div class="row">
                    <div class="col-xs-12">
                        <b>{{ _("PO Date") }}: </b>&nbsp;
                        {% if po.transaction_date %}
                            {{po.get_formatted('transaction_date')}}
                        {% endif %}
                    </div>
                </div>
            </td>
            <td>
                <div class="row">
                    <div class="col-xs-12">
                        <b>{{ _("Mode Of Transport") }}: </b>&nbsp;
                    </div>
                </div>
                <div class="row">
                    <div class="col-xs-12">
                        <b>{{ _("Vehicle No.") }}: </b>&nbsp;
                    </div>
                </div>
                <div class="row">
                    <div class="col-xs-12">
                        <b>{{ _("Transporter") }}: </b>&nbsp;
                    </div>
                </div>
            </td>
        </tr>
        <tr>
            <td class="text-center">
                <b>{{ _("Details of Consignor/Supplier:")}}</b>
            </td>
            <td class="text-center">
                <b>{{ _("Details of Recipent/Job-Worker:")}}</b>
            </td>
        </tr>
        <tr>
            <td>

                <b>{{ _("Name") }}:&nbsp;
                {% if address.address_title %}
                    {{address.address_title}}
                {% endif %}</b><br>
                <b>{{ _("Address") }}:</b>&nbsp;
                {% if address.address_line1 %}{{address.address_line1}}{% endif %}<br>
                {% if address.address_line1 %}{{address.address_line1}}<br>{% endif %}
                <b>{{ _("City") }}:&nbsp;</b>{% if address.city %}{{address.city}}{% endif %}
                {% if address.pincode %}&nbsp;-&nbsp;{{address.pincode}}{% endif %}<br>
                <b>{{ _("State") }}:</b>&nbsp;{{address.gst_state}}
                <b>{{ _("State Code") }}:</b>&nbsp;{{address.gst_state_number}}<br>
                <b>{{ _("GSTIN") }}:</b>&nbsp;{{address.gstin}}<br>
                <b>{{ _("PAN") }}:</b>&nbsp;
                {% if address.gstin %}
                    {{address.gstin[2:-3]}}
                {% endif %}<br>
            </td>
            <td>
                <b>{{ _("Name") }}:&nbsp;
                {% if sa.address_title %}
                    {{sa.address_title}}
                {% endif %}</b><br>
                <b>{{ _("Address") }}:</b>&nbsp;
                {% if sa.address_line1 %}{{sa.address_line1}}{% endif %}<br>
                {% if sa.address_line1 %}{{sa.address_line1}}<br>{% endif %}
                <b>{{ _("City") }}:&nbsp;</b>{% if sa.city %}{{sa.city}}{% endif %}
                {% if sa.pincode %}&nbsp;-&nbsp;{{sa.pincode}}{% endif %}<br>
                <b>{{ _("State") }}:</b>&nbsp;{% if sa.gst_state %}{{sa.gst_state}}{% endif %}
                <b>{{ _("State Code") }}:</b>&nbsp;
                {% if sa.gst_state_number %}{{sa.gst_state_number}}{% endif %}<br>
                <b>{{ _("GSTIN") }}:</b>&nbsp;{% if sa.gstin %}{{sa.gstin}}{% endif %}<br>
                {%- set sup=frappe.get_doc("Supplier",doc.supplier) %}
                <b>{{ _("PAN") }}:</b>&nbsp;{% if sup.pan %}{{sup.pan}}{% endif %}<br>
            </td>
        </tr>
    </table>
    <table style="margin-top: 2px;">
        <tr>
            <th><b>{{ _("Sr No.") }}</b></th>
            <th><b>{{ _("Item Code") }}</b></th>
            <th><b>{{ _("Description Of Goods") }}</b></th>
            <th><b>{{ _("Quantity") }}</b></th>
            <th><b>{{ _("Unit") }}</b></th>
            <th><b>{{ _("HSN") }}</b></th>
            <th><b>{{ _("Value of Goods(Rs.)") }}</b></th>
            <th><b>{{ _("Nature of Goods") }}</b></th>
        </tr>
        {%- for item in doc.items -%}
            <tr>
                <td style="text-align: center;">{{ item.idx }}</td>
                <td style="text-align: center;">{{ item.item_code }}</td>
                <td>
                    {% if item.description %}
                        {{item.description}}<br>
                    {% endif %}
                    {% if item.batch_no %}
                        Batch No.:-&nbsp;{{item.batch_no}}<br>
                    {% endif %}
                </td>
                <td style="text-align: center;">
                    {% if item.qty %}
                        {{ item.get_formatted("qty") }}
                    {% endif %}
                </td>
                <td style="text-align: center;">
                    {% if item.uom %}
                        {{ item.get_formatted("uom") }}
                    {% endif %}
                </td>
                <td style="text-align: center;">
                    {% set hsn %}{% endset %}
                    {% if item.item_code %}
                        {%- set hsn = frappe.get_doc("Item",item.item_code) %}
                    {% endif %}
                    {% if hsn.gst_hsn_code %}
                        {{ hsn.gst_hsn_code }}
                    {% endif %}
                </td>
                <td style="text-align: center;">
                    {% if item.basic_amount %}
                        {{ item.get_formatted("basic_amount") }}
                    {% endif %}
                </td>
                <td style="text-align: center;">
                    Input
                </td>
            </tr>
        {%- endfor -%}
        <tr>

            <th></th>
            <th></th>
            <th style="text-align: right;">Total:</th>
            <th style="text-align: center;">{{total_qty[0]}}</th>
            <th></th>
            <th></th>
            <th style="text-align: center;">{{total_amt[0]}}</th>
            <th></th>
        </tr>
    </table>
    <table style="margin-top: 2px;">
        <tr>
            <td><b>Remark :-</b></td>
        </tr>
        <tr>
            <td><b>Expected Duration of Processing / Manufacturing :<b> 365 Days.</td>
        </tr>
    </table>
    <table>
        <tr>
