css:
.textcenter{
        text-align:center;
}
.borderclass{
    border-bottom: 1px solid dodgerblue;
}
table {
    width:100%;
    text-align:left;
    border-collapse: collapse;
}

table, td, th {
    /*width:50%;*/
    border: 1px solid dodgerblue;
    font-size:10px;

}
.print-format{
    padding: 12px !important;
    margin-bottom: 0px;
    margin-top:0px;
}

html:
{% set b %}{% endset %}
{% if doc.quality_inspection%}
    {%- set b=frappe.get_doc("Quality Inspection Template",{"quality_inspection_template_name":doc.quality_inspection_template}) -%}
{% endif %}
<table>
    <tr>
    <td>

    {% if letter_head %}
        {{ letter_head }}
    {% endif %}

    </td>
    </tr>
</table>
<table>
    <tr>
        <td>
            <br>

            <div style="font-size: 16px;text-align: center; ">
                <b>CERTIFICATE OF ANALYSIS</b>
            </div>
            <br>
        </td>
    </tr>
</table>
<div>
    <table>
        <tr>
            <td style="width: 50%;">
                <div class="row">
                    <div class="col-xs-4">
                        <b>{{ _("Product") }}</b>
                    </div>
                    <div class="col-xs-8">
                        :&nbsp;
                        {% if doc.item_name %}
                            {{doc.item_name}}
                        {% endif %}
                    </div>
                </div>
                <div class="row">
                    <div class="col-xs-4">
                        <b>{{ _("Reference Type") }}</b>
                    </div>
                    <div class="col-xs-8">
                        :&nbsp;
                        {% if doc.reference_type %}
                            {{doc.reference_type}}
                        {% endif %}
                    </div>
                </div>
                <div class="row">
                    <div class="col-xs-4">
                        <b>{{ _("Reference Name") }}</b>
                    </div>
                    <div class="col-xs-8">
                        :&nbsp;
                        {% if doc.reference_name %}
                            {{doc.reference_name}}
                        {% endif %}
                    </div>
                </div>
                <div class="row">
                    <div class="col-xs-4">
                        <b>{{ _("Sampled By") }}</b>
                    </div>
                    <div class="col-xs-8">
                        :&nbsp;

                    </div>
                </div>
            </td>
            <td style="width: 50%;">
                <div class="row">
                    <div class="col-xs-4">
                        <b>{{ _("BATCH NO") }}</b>
                    </div>
                    <div class="col-xs-8">
                        :&nbsp;
                        {% if doc.batch_no %}
                            {{doc.batch_no}}
                        {% endif %}
                    </div>
                </div>
                <div class="row">
                    <div class="col-xs-4">
                        <b>{{ _("Date of Mfg.") }}</b>
                    </div>
                    <div class="col-xs-8">
                        :&nbsp;{% if doc.report_date %}
                                    {{ doc.get_formatted("report_date") }}
                                {% endif %}
                    </div>
                </div>
                <div class="row">
                    <div class="col-xs-4">
                        <b>{{ _("Date of Sampling") }}</b>
                    </div>
                    <div class="col-xs-8">
                        :&nbsp;{% if doc.expiry_date %}
                                    {{ doc.get_formatted("expiry_date") }}
                                {% endif %}
                    </div>
                </div>
                <div class="row">
                    <div class="col-xs-4">
                        <b>{{ _("Sample Quantity") }}</b>
                    </div>
                    <div class="col-xs-8">
                         :&nbsp;{% if doc.sample_size %}
                            {{doc.sample_size}}
                        {% endif %}
                    </div>
                </div>
                <div class="row">
                    <div class="col-xs-4">
                        <b>{{ _("Packaging Details") }}</b>
                    </div>
                    <div class="col-xs-8">
                         :&nbsp;
                    </div>
                </div>
                <br>
            </td>
        </tr>
    </table>
    <table>
        <tr>
            <th style="width: 5%;"><b>{{ _("Sr No.") }}</b></th>
            <th style="width: 30%;"><b>{{ _("Test Parameters") }}</b></th>
            <th style="width: 20%;"><b>{{ _("Specification") }}</b></th>
            <th style="width: 25%;"><b>{{ _("Result") }}</b></th>
            <th style="width: 25%;"><b>{{ _("Remark") }}</b></th>
        </tr>
        {% for i in doc.readings %}
            <tr>
                <td>{{i.idx}}</td>
                <td>
                    {% if i.specification %}
                        {{i.specification}}
                    {% endif %}
                </td>
                 <td>
                        {% if i.min_value>0 or i.max_value>0 %}
                                    between {{i.min_value}} and {{i.max_value}}
                                {% elif i.acceptance_formula %}
                                    {{i.acceptance_formula}}
                                {% elif i.description %}
                                    {{i.description}}
                                {%else %}
                                    {% if i.value %}
                                    {{i.value}}
                                    {% endif %}  
                                {% endif %}
                </td>
                <td>
                    {% if i.reading_1 %}
                        {% if i.reading_1 %}
                        <b>reading1:</b> {{i.reading_1}}<br>
                        {% endif %}
                        {% if i.reading_2 %}
                        <b>reading2:</b> {{i.reading_2}}<br>
                        {% endif %}
                        {% if i.reading_3 %}
                        <b>reading3:</b> {{i.reading_3}}<br>
                        {% endif %}
                        {% if i.reading_4 %}
                        <b>reading4:</b> {{i.reading_4}}<br>
                        {% endif %}
                        {% if i.reading_5 %}
                        <b>reading5:</b> {{i.reading_5}}<br>
                        {% endif %}
                        {% if i.reading_6 %}
                        <b>reading6:</b> {{i.reading_6}}<br>
                        {% endif %}
                        {% if i.reading_7 %}
                        <b>reading7:</b> {{i.reading_7}}<br>
                        {% endif %}
                        {% if i.reading_8 %}
                        <b>reading8:</b> {{i.reading_8}}<br>
                        {% endif %}
                        {% if i.reading_9 %}
                        <b>reading9:</b> {{i.reading_9}}<br>
                        {% endif %}
                        {% if i.reading_10 %}
                        <b>reading10:</b> {{i.reading_10}}<br>
                        {% endif %}
                    {% elif i.reading_value %}
                        {{i.reading_value}}
                    {% else %}
                        {{i.parameter_value}}
                    {% endif %}
                </td>
                <td>
                    {% if i.status %}
                        {{i.status}}
                    {% endif %}
                </td>
            </tr>
        {% endfor %}

    </table>
    <table>
        <tr>
            <td style="width: 80%;">
                <b>
                    The product confirms to the specification and is of standard quality.
                </b>
            </td>
            <td style="width: 20%; text-align: center; ">
                <br><br>
                {% if doc.owner %}
                    {{doc.owner}}
                {% endif %}<br>
                Tested by
            </td>
        </tr>
    </table>
</div>

<br>
<br>
 <table>
        <tr>
            <td>
                Prepared By<br>
                {% if doc.owner %}
                    {{ frappe.get_fullname(doc.owner) or "" }}
                {% endif %}

            </td>
            <td>
                Approved By<br>
                {% if doc.docstatus == 1 %}
                    {% if doc.modified_by %}
                        {{ frappe.get_fullname(doc.modified_by) or "" }}
                    {% endif %}
                {% endif %}

            </td>
        </tr>
    </table>
